    return (

        <View
            style={{
                height: windowHeight,
                width: windowWidth,
                justifyContent: "center",
                alignItems: "center",
            }}
        >
            <Image
                source={require('../assets/img/i9.jpg')}
                style={{ width: '100%', height: '200px' }}
            ></Image>
            <Text style={{ fontSize: 24 }}>{data.title}</Text>
